const express = require('express');
const app = express();
const cors = require('cors');
const bodyParser = require('body-parser');
const jwt = require('jsonwebtoken');
const SECRET = 'ton_secret_jwt';
const users = []; // stockage en mémoire (à remplacer par une vraie BDD)
let tasks = [];
let ideas = [];
app.use(cors());
app.use(bodyParser.json());

// --- AUTHENTIFICATION ---

// Inscription
app.post('/api/register', (req, res) => {
  const {
    name,
    email,
    password
  } = req.body;
  if (!name || !email || !password) {
    return res.status(400).json({
      message: 'Tous les champs sont obligatoires (name, email, password)'
    });
  }
  if (users.find(u => u.email === email)) {
    return res.status(400).json({
      message: 'Email déjà utilisé'
    });
  }
  users.push({
    name,
    email,
    password
  }); // WARNING: pas de hash ici, à faire en prod !
  res.json({
    message: 'Utilisateur créé avec succès'
  });
});

// Connexion
app.post('/api/login', (req, res) => {
  const {
    email,
    password
  } = req.body;
  if (!email || !password) {
    return res.status(400).json({
      message: 'Email et mot de passe sont obligatoires'
    });
  }
  const user = users.find(u => u.email === email && u.password === password);
  if (!user) {
    return res.status(401).json({
      message: 'Email ou mot de passe invalide'
    });
  }
  const token = jwt.sign({
    email: user.email,
    name: user.name
  }, SECRET, {
    expiresIn: '1h'
  });
  res.json({
    token
  });
});

// Middleware d'authentification JWT
function authenticateToken(req, res, next) {
  const authHeader = req.headers['authorization'];
  const token = authHeader && authHeader.split(' ')[1]; // Bearer TOKEN

  if (!token) return res.status(401).json({
    message: 'Token manquant'
  });
  jwt.verify(token, SECRET, (err, user) => {
    if (err) return res.status(403).json({
      message: 'Token invalide'
    });
    req.user = user; // payload décodé
    next();
  });
}

// --- ROUTES TASKS ---

// Récupérer toutes les tâches de l'utilisateur
app.get('/api/tasks', authenticateToken, (req, res) => {
  const userEmail = req.user.email;
  const userTasks = tasks.filter(task => task.owner === userEmail);
  res.json(userTasks);
});

// Créer une tâche
app.post('/api/tasks', authenticateToken, (req, res) => {
  const {
    title,
    description,
    dueDate,
    status
  } = req.body;
  const newTask = {
    id: tasks.length + 1,
    title,
    description,
    dueDate,
    status,
    owner: req.user.email
  };
  tasks.push(newTask);
  res.status(201).json(newTask);
});

// Modifier une tâche
app.put('/api/tasks/:id', authenticateToken, (req, res) => {
  const id = parseInt(req.params.id);
  const task = tasks.find(t => t.id === id && t.owner === req.user.email);
  if (!task) return res.status(404).json({
    message: 'Tâche non trouvée'
  });
  const {
    title,
    description,
    dueDate,
    status
  } = req.body;
  if (title !== undefined) task.title = title;
  if (description !== undefined) task.description = description;
  if (dueDate !== undefined) task.dueDate = dueDate;
  if (status !== undefined) task.status = status;
  res.json(task);
});

// Supprimer une tâche
app.delete('/api/tasks/:id', authenticateToken, (req, res) => {
  const id = parseInt(req.params.id);
  const index = tasks.findIndex(t => t.id === id && t.owner === req.user.email);
  if (index === -1) return res.status(404).json({
    message: 'Tâche non trouvée'
  });
  tasks.splice(index, 1);
  res.json({
    message: 'Tâche supprimée'
  });
});

// --- ROUTES IDEAS ---

// Récupérer toutes les idées de l'utilisateur
app.get('/api/ideas', authenticateToken, (req, res) => {
  const userEmail = req.user.email;
  const userIdeas = ideas.filter(idea => idea.owner === userEmail);
  res.json(userIdeas);
});

// Créer une idée
app.post('/api/ideas', authenticateToken, (req, res) => {
  const {
    title,
    description
  } = req.body;
  const newIdea = {
    id: ideas.length + 1,
    title,
    description,
    owner: req.user.email
  };
  ideas.push(newIdea);
  res.status(201).json(newIdea);
});

// Modifier une idée
app.put('/api/ideas/:id', authenticateToken, (req, res) => {
  const id = parseInt(req.params.id);
  const idea = ideas.find(i => i.id === id && i.owner === req.user.email);
  if (!idea) return res.status(404).json({
    message: 'Idée non trouvée'
  });
  const {
    title,
    description
  } = req.body;
  if (title !== undefined) idea.title = title;
  if (description !== undefined) idea.description = description;
  res.json(idea);
});

// Supprimer une idée
app.delete('/api/ideas/:id', authenticateToken, (req, res) => {
  const id = parseInt(req.params.id);
  const index = ideas.findIndex(i => i.id === id && i.owner === req.user.email);
  if (index === -1) return res.status(404).json({
    message: 'Idée non trouvée'
  });
  ideas.splice(index, 1);
  res.json({
    message: 'Idée supprimée'
  });
});

// Démarrage du serveur
const PORT = process.env.PORT || 5000;
app.listen(PORT, () => {
  console.log(`Serveur démarré sur le port ${PORT}`);
});

