import React, { useState, useEffect } from 'react';
import Navbar from './components/Navbar';
import Login from './components/Login';
import Register from './components/Register';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';
import { getTasks, createTask, updateTask, deleteTask, getIdeas } from './services/api';

export default function App() {
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [view, setView] = useState(token ? 'tasks' : 'login'); // login | register | tasks
  const [tasks, setTasks] = useState([]);
  const [editingTask, setEditingTask] = useState(null);
  const [message, setMessage] = useState(null); // {type: 'error'|'success', text: ''}

  useEffect(() => {
    if (message) {
      const timer = setTimeout(() => setMessage(null), 4000);
      return () => clearTimeout(timer);
    }
  }, [message]);

  useEffect(() => {
    if (!token) return;
    if (view === 'tasks') {
      getTasks(token).then(data => {
        setTasks(Array.isArray(data) ? data : []);
      });
    }
  }, [view, token]);

  const handleLogin = async (jwt) => {
    localStorage.setItem('token', jwt);
    setToken(jwt);
    setView('tasks');
    const fetchedTasks = await getTasks(jwt);
    setTasks(Array.isArray(fetchedTasks) ? fetchedTasks : []);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setToken('');
    setTasks([]);
    setEditingTask(null);
    setView('login');
  };

  const handleSaveTask = async (taskData) => {
    if (editingTask) {
      const updated = await updateTask(token, editingTask._id, taskData);
      if (!updated.error) {
        setTasks(tasks.map(t => (t._id === updated._id ? updated : t)));
        setEditingTask(null);
        setMessage({ type: 'success', text: 'Tâche mise à jour avec succès !' });
      } else {
        setMessage({ type: 'error', text: `Erreur mise à jour: ${updated.error}` });
      }
    } else {
      const created = await createTask(token, taskData);
      if (!created.error) {
        setTasks([...tasks, created]);
        setEditingTask(null);
        setMessage({ type: 'success', text: 'Tâche créée avec succès !' });
      } else {
        setMessage({ type: 'error', text: `Erreur création: ${created.error}` });
      }
    }
  };

  const handleDeleteTask = async (taskId) => {
    if (!window.confirm('Voulez-vous vraiment supprimer cette tâche ?')) return;

    const deleted = await deleteTask(token, taskId);
    if (!deleted.error) {
      setTasks(tasks.filter(t => t._id !== taskId));
      setMessage({ type: 'success', text: 'Tâche supprimée avec succès !' });
    } else {
      setMessage({ type: 'error', text: `Erreur suppression: ${deleted.error}` });
    }
  };

  return (
    <div style={{ maxWidth: 800, margin: 'auto', padding: 20 }}>
      <Navbar setView={setView} token={token} handleLogout={handleLogout} />

      {message && (
        <div style={{
          marginBottom: 10,
          padding: 10,
          color: message.type === 'error' ? 'red' : 'green',
          border: `1px solid ${message.type === 'error' ? 'red' : 'green'}`,
          borderRadius: 5,
        }}>
          {message.text}
        </div>
      )}

      {!token && view === 'login' && <Login onLogin={handleLogin} />}
      {!token && view === 'register' && <Register onRegister={() => setView('login')} />}

      {token && view === 'tasks' && (
        <>
          <TaskForm
            token={token}
            onSave={handleSaveTask}
            editingTask={editingTask}
            cancelEdit={() => setEditingTask(null)}
          />
          <TaskList
            token={token}
            tasks={tasks}
            setTasks={setTasks}
            onEdit={setEditingTask}
            onDelete={handleDeleteTask}
          />
        </>
      )}
    </div>
  );
}
