import React, { useState, useEffect } from 'react';
import Navbar from './components/Navbar';
import IdeaForm from './components/IdeaForm';
import IdeaTable from './components/IdeaTable';
import IdeaDetailsBox from './components/IdeaDetailsBox';

export default function App() {
  const [view, setView] = useState('login'); // login | register | ideas (après login)
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [ideas, setIdeas] = useState([]);
  const [editingIdea, setEditingIdea] = useState(null);
  const [showDetailsIdea, setShowDetailsIdea] = useState(null);

  // Charger idées depuis backend
  const fetchIdeas = async () => {
    if (!token) return;
    try {
      const res = await fetch('/api/ideas', { headers: { Authorization: `Bearer ${token}` } });
      if (res.ok) {
        const data = await res.json();
        setIdeas(data);
      }
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    if (token) {
      setView('ideas');
      fetchIdeas();
    } else {
      setView('login');
    }
  }, [token]);

  // Ajouter ou modifier une idée
  const saveIdea = async (ideaData) => {
    const method = editingIdea ? 'PUT' : 'POST';
    const url = editingIdea ? `/api/ideas/${editingIdea._id}` : '/api/ideas';
    try {
      const res = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(ideaData),
      });
      if (res.ok) {
        fetchIdeas();
        setEditingIdea(null);
      } else {
        alert('Erreur lors de la sauvegarde');
      }
    } catch {
      alert('Erreur réseau');
    }
  };

  // Supprimer une idée
  const deleteIdea = async (id) => {
    if (!window.confirm('Supprimer cette idée ?')) return;
    try {
      const res = await fetch(`/api/ideas/${id}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` },
      });
      if (res.ok) {
        fetchIdeas();
      } else {
        alert('Erreur lors de la suppression');
      }
    } catch {
      alert('Erreur réseau');
    }
  };

  // Gestion login/register/token (à compléter selon ta logique)

  return (
    <div>
      <Navbar setView={setView} />
      {view === 'ideas' && (
        <>
          <IdeaForm
            token={token}
            onSave={saveIdea}
            editingIdea={editingIdea}
            cancelEdit={() => setEditingIdea(null)}
          />
          <IdeaTable
            ideas={ideas}
            onEdit={setEditingIdea}
            onDelete={deleteIdea}
            onShowDetails={setShowDetailsIdea}
          />
          <IdeaDetailsBox idea={showDetailsIdea} onClose={() => setShowDetailsIdea(null)} />
        </>
      )}

      {/* Ici gérer la vue login/register comme avant */}
    </div>
  );
}
