import React, { useState, useEffect } from 'react';

const fakeApi = {
  tasks: [
    { id: 1, title: 'Tâche 1', status: 'pending', dueDate: '2025-08-21', description: 'Description de la tâche 1' },
    { id: 2, title: 'Tâche 2', status: 'done', dueDate: '2025-09-01', description: 'Description de la tâche 2' },
  ],
  createTask(task) {
    return new Promise(resolve => {
      const newTask = { ...task, id: Date.now() };
      this.tasks.push(newTask);
      setTimeout(() => resolve(newTask), 500);
    });
  },
  updateTask(id, updated) {
    return new Promise(resolve => {
      this.tasks = this.tasks.map(t => (t.id === id ? { ...t, ...updated } : t));
      setTimeout(() => resolve(this.tasks.find(t => t.id === id)), 500);
    });
  },
  deleteTask(id) {
    return new Promise(resolve => {
      this.tasks = this.tasks.filter(t => t.id !== id);
      setTimeout(() => resolve({ success: true }), 500);
    });
  },
  getTasks() {
    return new Promise(resolve => {
      setTimeout(() => resolve(this.tasks), 500);
    });
  }
};

const statusColors = {
  'pending': '#f0ad4e',       // orange
  'in-progress': '#0275d8',   // bleu
  'done': '#5cb85c',          // vert
};

function TaskForm({ onSave, editingTask, cancelEdit }) {
  const [title, setTitle] = useState('');
  const [status, setStatus] = useState('pending');
  const [dueDate, setDueDate] = useState('');
  const [description, setDescription] = useState('');

  useEffect(() => {
    if (editingTask) {
      setTitle(editingTask.title || '');
      setStatus(editingTask.status || 'pending');
      setDueDate(editingTask.dueDate ? editingTask.dueDate.substring(0, 10) : '');
      setDescription(editingTask.description || '');
    } else {
      setTitle('');
      setStatus('pending');
      setDueDate('');
      setDescription('');
    }
  }, [editingTask]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!title.trim()) {
      alert('Le titre est obligatoire');
      return;
    }
    if (!status) {
      alert('Le statut est obligatoire');
      return;
    }
    onSave({ title, status, dueDate, description });
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: 20, border: '1px solid #ccc', padding: 15, borderRadius: 6 }}>
      <h3>{editingTask ? 'Modifier une tâche' : 'Ajouter une tâche'}</h3>

      <div style={{ marginBottom: 10 }}>
        <label>Titre :</label><br />
        <input value={title} onChange={e => setTitle(e.target.value)} required style={{ width: '100%', padding: 5 }} />
      </div>

      <div style={{ marginBottom: 10 }}>
        <label>Statut :</label><br />
        <select value={status} onChange={e => setStatus(e.target.value)} required style={{ width: '100%', padding: 5 }}>
          <option value="pending">En attente</option>
          <option value="in-progress">En cours</option>
          <option value="done">Terminé</option>
        </select>
      </div>

      <div style={{ marginBottom: 10 }}>
        <label>Date limite :</label><br />
        <input type="date" value={dueDate} onChange={e => setDueDate(e.target.value)} style={{ width: '100%', padding: 5 }} />
      </div>

      <div style={{ marginBottom: 10 }}>
        <label>Description :</label><br />
        <textarea value={description} onChange={e => setDescription(e.target.value)} style={{ width: '100%', padding: 5 }} />
      </div>

      <button type="submit" style={{ padding: '5px 10px' }}>{editingTask ? 'Mettre à jour' : 'Enregistrer'}</button>

      {editingTask && (
        <button type="button" onClick={cancelEdit} style={{ padding: '5px 10px', marginLeft: 10, background: '#ccc' }}>Annuler</button>
      )}
    </form>
  );
}

export default function App() {
  const [tasks, setTasks] = useState([]);
  const [editingTask, setEditingTask] = useState(null);

  useEffect(() => {
    fakeApi.getTasks().then(setTasks);
  }, []);

  const handleSave = (taskData) => {
    if (editingTask) {
      fakeApi.updateTask(editingTask.id, taskData).then(updated => {
        setTasks(tasks.map(t => (t.id === updated.id ? updated : t)));
        setEditingTask(null);
      });
    } else {
      fakeApi.createTask(taskData).then(newTask => {
        setTasks([...tasks, newTask]);
      });
    }
  };

  const handleDelete = (id) => {
    if (window.confirm('Confirmer la suppression ?')) {
      fakeApi.deleteTask(id).then(() => {
        setTasks(tasks.filter(t => t.id !== id));
      });
    }
  };

  const handleEdit = (task) => {
    setEditingTask(task);
  };

  const cancelEdit = () => {
    setEditingTask(null);
  };

  return (
    <div style={{ maxWidth: 900, margin: 'auto', padding: 20, fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif" }}>
      <TaskForm onSave={handleSave} editingTask={editingTask} cancelEdit={cancelEdit} />

      <table style={{ width: '100%', borderCollapse: 'collapse', boxShadow: '0 0 10px rgba(0,0,0,0.1)' }}>
        <thead>
          <tr style={{ backgroundColor: '#007BFF', color: '#fff', textAlign: 'left' }}>
            <th style={{ padding: '10px' }}>Titre</th>
            <th style={{ padding: '10px' }}>Statut</th>
            <th style={{ padding: '10px' }}>Date limite</th>
            <th style={{ padding: '10px' }}>Description</th>
            <th style={{ padding: '10px' }}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {tasks.length > 0 ? (
            tasks.map(task => (
              <tr key={task.id} style={{ borderBottom: '1px solid #ddd' }}>
                <td style={{ padding: '8px' }}>{task.title || 'Titre manquant'}</td>
                <td style={{ padding: '8px' }}>
                  <span
                    style={{
                      padding: '4px 8px',
                      borderRadius: 12,
                      color: '#fff',
                      backgroundColor: statusColors[task.status] || '#6c757d',
                      fontWeight: 'bold',
                      textTransform: 'capitalize',
                      fontSize: '0.9rem',
                      display: 'inline-block',
                      minWidth: 90,
                      textAlign: 'center',
                    }}
                  >
                    {task.status || 'Statut manquant'}
                  </span>
                </td>
                <td style={{ padding: '8px' }}>{task.dueDate ? task.dueDate.substring(0, 10) : 'Date manquante'}</td>
                <td style={{ padding: '8px' }}>{task.description || 'Description manquante'}</td>
                <td style={{ padding: '8px' }}>
                  <button onClick={() => handleEdit(task)} style={{ cursor: 'pointer', marginRight: 8 }}>✏️</button>
                  <button onClick={() => handleDelete(task.id)} style={{ cursor: 'pointer' }}>🗑️</button>
                </td>
              </tr>
            ))
          ) : (
            <tr><td colSpan="5" style={{ textAlign: 'center', padding: 20 }}>Aucune tâche disponible</td></tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
