import React, { useState, useEffect } from "react";

// ======== Fake API simulée ========
const fakeApi = {
  users: [],
  tasks: [],
  register(name, email, password) {
    return new Promise((resolve) => {
      setTimeout(() => {
        if (this.users.some((u) => u.email === email)) {
          resolve({ error: "Cet email est déjà utilisé." });
        } else {
          this.users.push({ name, email, password });
          resolve({ message: "Inscription réussie" });
        }
      }, 500);
    });
  },
  login(email, password) {
    return new Promise((resolve) => {
      setTimeout(() => {
        const user = this.users.find(
          (u) => u.email === email && u.password === password
        );
        if (user) {
          resolve({ token: "fake-jwt-token", name: user.name });
        } else {
          resolve({ message: "Email ou mot de passe incorrect" });
        }
      }, 500);
    });
  },
  getTasks() {
    return new Promise((resolve) => {
      setTimeout(() => resolve(this.tasks), 300);
    });
  },
  createTask(task) {
    return new Promise((resolve) => {
      const newTask = { ...task, id: Date.now() };
      this.tasks.push(newTask);
      setTimeout(() => resolve(newTask), 300);
    });
  },
  updateTask(id, updated) {
    return new Promise((resolve) => {
      this.tasks = this.tasks.map((t) =>
        t.id === id ? { ...t, ...updated } : t
      );
      setTimeout(() => resolve(this.tasks.find((t) => t.id === id)), 300);
    });
  },
  deleteTask(id) {
    return new Promise((resolve) => {
      this.tasks = this.tasks.filter((t) => t.id !== id);
      setTimeout(() => resolve({ success: true }), 300);
    });
  },
};

// ======== Navbar ========
function Navbar({ onLogout, userName }) {
  return (
    <nav
      style={{
        backgroundColor: "#007BFF",
        padding: "10px 20px",
        color: "#fff",
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        fontWeight: "bold",
        fontSize: "1.2rem",
        marginBottom: 20,
        borderRadius: 6,
      }}
    >
      <div>Mon App Tâches - Bonjour {userName}</div>
      <button
        onClick={() => {
          if (window.confirm("Voulez-vous vraiment vous déconnecter ?")) {
            onLogout();
          }
        }}
        style={{
          backgroundColor: "#dc3545",
          color: "white",
          border: "none",
          padding: "6px 12px",
          borderRadius: 4,
          cursor: "pointer",
        }}
      >
        Déconnexion
      </button>
    </nav>
  );
}

// ======== Login ========
function Login({ onLogin }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    const result = await fakeApi.login(email, password);
    if (result.token) {
      onLogin(result.token, result.name);
    } else {
      setError(result.message || "Erreur de connexion");
    }
  };

  return (
    <div style={{ maxWidth: 350, margin: "40px auto", padding: 20, border: "1px solid #ccc", borderRadius: 8, backgroundColor: "#fff" }}>
      <h2 style={{ textAlign: "center", marginBottom: 20 }}>Connexion</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={{ width: "100%", padding: 10, marginBottom: 15, borderRadius: 5, border: "1px solid #ccc" }}
        />
        <input
          type="password"
          placeholder="Mot de passe"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={{ width: "100%", padding: 10, marginBottom: 15, borderRadius: 5, border: "1px solid #ccc" }}
        />
        <button type="submit" style={{ width: "100%", padding: 10, backgroundColor: "#007BFF", color: "white", fontWeight: "bold", border: "none", borderRadius: 5, cursor: "pointer" }}>
          Se connecter
        </button>
        {error && <p style={{ color: "red", marginTop: 15, textAlign: "center" }}>{error}</p>}
      </form>
    </div>
  );
}

// ======== Register ========
function Register({ onRegister }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    const result = await fakeApi.register(name, email, password);
    if (result.message) {
      setSuccess("Inscription réussie ! Vous pouvez vous connecter.");
      setName("");
      setEmail("");
      setPassword("");
      setTimeout(() => {
        onRegister();
      }, 1500);
    } else {
      setError(result.error || "Erreur lors de l'inscription");
    }
  };

  return (
    <div style={{ maxWidth: 350, margin: "40px auto", padding: 20, border: "1px solid #ccc", borderRadius: 8, backgroundColor: "#fff" }}>
      <h2 style={{ textAlign: "center", marginBottom: 20 }}>Inscription</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Nom" value={name} onChange={(e) => setName(e.target.value)} required style={{ width: "100%", padding: 10, marginBottom: 15, borderRadius: 5, border: "1px solid #ccc" }} />
        <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required style={{ width: "100%", padding: 10, marginBottom: 15, borderRadius: 5, border: "1px solid #ccc" }} />
        <input type="password" placeholder="Mot de passe" value={password} onChange={(e) => setPassword(e.target.value)} required style={{ width: "100%", padding: 10, marginBottom: 15, borderRadius: 5, border: "1px solid #ccc" }} />
        <button type="submit" style={{ width: "100%", padding: 10, backgroundColor: "#007BFF", color: "white", fontWeight: "bold", border: "none", borderRadius: 5, cursor: "pointer" }}>
          S'inscrire
        </button>
        {success && <p style={{ color: "green", marginTop: 15, textAlign: "center" }}>{success}</p>}
        {error && <p style={{ color: "red", marginTop: 15, textAlign: "center" }}>{error}</p>}
      </form>
    </div>
  );
}

// ======== TaskFilter ========
function TaskFilter({ onFilterChange }) {
  const [statusFilter, setStatusFilter] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [sortField, setSortField] = useState("");
  const [sortOrder, setSortOrder] = useState("asc");

  useEffect(() => {
    onFilterChange({ status: statusFilter, search: searchTerm, sortField, sortOrder });
  }, [statusFilter, searchTerm, sortField, sortOrder, onFilterChange]);

  return (
    <div style={{ marginBottom: 20, display: "flex", gap: 10, flexWrap: "wrap", alignItems: "center" }}>
      <select value={statusFilter} onChange={(e) => setStatusFilter(e.target.value)} style={{ padding: "6px" }}>
        <option value="">Tous les statuts</option>
        <option value="pending">En attente</option>
        <option value="in-progress">En cours</option>
        <option value="done">Terminé</option>
      </select>

      <input type="text" placeholder="Rechercher..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} style={{ padding: "6px", flexGrow: 1, minWidth: 150 }} />

      <select value={sortField} onChange={(e) => setSortField(e.target.value)} style={{ padding: "6px" }}>
        <option value="">Trier par</option>
        <option value="title">Titre</option>
        <option value="dueDate">Date limite</option>
        <option value="status">Statut</option>
      </select>

      <select value={sortOrder} onChange={(e) => setSortOrder(e.target.value)} style={{ padding: "6px" }} disabled={!sortField}>
        <option value="asc">Ascendant</option>
        <option value="desc">Descendant</option>
      </select>
    </div>
  );
}

// ======== TaskForm ========
function TaskForm({ onSave, editingTask, cancelEdit }) {
  const [title, setTitle] = useState("");
  const [status, setStatus] = useState("pending");
  const [dueDate, setDueDate] = useState("");
  const [description, setDescription] = useState("");

  useEffect(() => {
    if (editingTask) {
      setTitle(editingTask.title || "");
      setStatus(editingTask.status || "pending");
      setDueDate(editingTask.dueDate ? editingTask.dueDate.substring(0, 10) : "");
      setDescription(editingTask.description || "");
    } else {
      setTitle("");
      setStatus("pending");
      setDueDate("");
      setDescription("");
    }
  }, [editingTask]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!title.trim()) {
      alert("Le titre est obligatoire");
      return;
    }
    onSave({ title, status, dueDate, description });
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: 20, border: "1px solid #ccc", padding: 15, borderRadius: 6, backgroundColor: "#fff" }}>
      <h3>{editingTask ? "Modifier une tâche" : "Ajouter une tâche"}</h3>

      <div style={{ marginBottom: 10 }}>
        <label>Titre :</label>
        <br />
        <input value={title} onChange={(e) => setTitle(e.target.value)} required style={{ width: "100%", padding: 5 }} />
      </div>

      <div style={{ marginBottom: 10 }}>
        <label>Statut :</label>
        <br />
        <select value={status} onChange={(e) => setStatus(e.target.value)} required style={{ width: "100%", padding: 5 }}>
          <option value="pending">En attente</option>
                  <option value="in-progress">En cours</option>
          <option value="done">Terminé</option>
        </select>
      </div>

      <div style={{ marginBottom: 10 }}>
        <label>Date limite :</label>
        <br />
        <input type="date" value={dueDate} onChange={(e) => setDueDate(e.target.value)} style={{ width: "100%", padding: 5 }} />
      </div>

      <div style={{ marginBottom: 10 }}>
        <label>Description :</label>
        <br />
        <textarea value={description} onChange={(e) => setDescription(e.target.value)} rows={3} style={{ width: "100%", padding: 5 }} />
      </div>

      <div>
        <button type="submit" style={{ marginRight: 10, padding: "6px 12px", border: "none", borderRadius: 4, backgroundColor: "#007BFF", color: "#fff", cursor: "pointer" }}>
          {editingTask ? "Mettre à jour" : "Ajouter"}
        </button>
        {editingTask && (
          <button type="button" onClick={cancelEdit} style={{ padding: "6px 12px", border: "none", borderRadius: 4, backgroundColor: "#6c757d", color: "#fff", cursor: "pointer" }}>
            Annuler
          </button>
        )}
      </div>
    </form>
  );
}
// ======== TaskList ========
const statusColors = { pending: "#f0ad4e", "in-progress": "#0275d8", done: "#5cb85c" };

function TaskList({ tasks, onEdit, onDelete }) {
  return (
    <table style={{ width: "100%", borderCollapse: "collapse", boxShadow: "0 0 10px rgba(0,0,0,0.1)", backgroundColor: "#fff" }}>
      <thead>
        <tr style={{ backgroundColor: "#007BFF", color: "#fff", textAlign: "left" }}>
          <th style={{ padding: "10px" }}>Titre</th>
          <th style={{ padding: "10px" }}>Statut</th>
          <th style={{ padding: "10px" }}>Date limite</th>
          <th style={{ padding: "10px" }}>Description</th>
          <th style={{ padding: "10px" }}>Actions</th>
        </tr>
      </thead>
      <tbody>
        {tasks.length > 0 ? (
          tasks.map((task) => (
            <tr key={task.id} style={{ borderBottom: "1px solid #ddd", cursor: "default" }}>
              <td style={{ padding: "8px" }}>{task.title || "Titre manquant"}</td>
              <td style={{ padding: "8px" }}>
                <span style={{ padding: "4px 8px", borderRadius: 12, color: "#fff", backgroundColor: statusColors[task.status] || "#6c757d", fontWeight: "bold", textTransform: "capitalize", fontSize: "0.9rem", display: "inline-block", minWidth: 90, textAlign: "center" }}>
                  {task.status || "Statut manquant"}
                </span>
              </td>
              <td style={{ padding: "8px" }}>{task.dueDate ? task.dueDate.substring(0, 10) : "Date manquante"}</td>
              <td style={{ padding: "8px" }}>{task.description || "Description manquante"}</td>
              <td style={{ padding: "8px" }}>
                <button onClick={() => onEdit(task)} style={{ cursor: "pointer", marginRight: 8, backgroundColor: "#ffc107", border: "none", borderRadius: 4, padding: "4px 8px" }}>✏️</button>
                <button onClick={() => { if (window.confirm("Confirmer la suppression ?")) onDelete(task.id); }} style={{ cursor: "pointer", backgroundColor: "#dc3545", border: "none", borderRadius: 4, padding: "4px 8px", color: "#fff" }}>🗑️</button>
              </td>
            </tr>
          ))
        ) : (
          <tr>
            <td colSpan="5" style={{ textAlign: "center", padding: 10 }}>Aucune tâche disponible</td>
          </tr>
        )}
      </tbody>
    </table>
  );
}

// ======== App ========
export default function App() {
  const [view, setView] = useState("login"); // "login" | "register" | "dashboard"
  const [token, setToken] = useState(null);
  const [userName, setUserName] = useState("");
  const [tasks, setTasks] = useState([]);
  const [editingTask, setEditingTask] = useState(null);
  const [filter, setFilter] = useState({ status: "", search: "", sortField: "", sortOrder: "asc" });

  useEffect(() => {
    if (token) {
      fetchTasks();
    }
  }, [token]);

  const fetchTasks = async () => {
    let data = await fakeApi.getTasks();

    // Filtrage
    if (filter.status) {
      data = data.filter((t) => t.status === filter.status);
    }
    if (filter.search) {
      data = data.filter((t) => t.title.toLowerCase().includes(filter.search.toLowerCase()));
    }
    // Tri
    if (filter.sortField) {
      data.sort((a, b) => {
        let aField = a[filter.sortField] || "";
        let bField = b[filter.sortField] || "";
        if (filter.sortField === "dueDate") {
          aField = aField || "9999-12-31";
          bField = bField || "9999-12-31";
        }
        if (aField < bField) return filter.sortOrder === "asc" ? -1 : 1;
        if (aField > bField) return filter.sortOrder === "asc" ? 1 : -1;
        return 0;
      });
    }

    setTasks(data);
  };

  const handleSaveTask = async (task) => {
    if (editingTask) {
      await fakeApi.updateTask(editingTask.id, task);
      setEditingTask(null);
    } else {
      await fakeApi.createTask(task);
    }
    fetchTasks();
  };

  const handleDeleteTask = async (id) => {
    await fakeApi.deleteTask(id);
    fetchTasks();
  };

  const handleEditTask = (task) => {
    setEditingTask(task);
  };

  const handleLogout = () => {
    setToken(null);
    setUserName("");
    setView("login");
    setTasks([]);
  };

  // ======== Vue ========
  if (!token) {
    return view === "login" ? <Login onLogin={(t, name) => { setToken(t); setUserName(name); setView("dashboard"); }} /> : <Register onRegister={() => setView("login")} />;
  }

  return (
    <div style={{ maxWidth: 900, margin: "20px auto", padding: 20 }}>
      <Navbar onLogout={handleLogout} userName={userName} />
      <TaskForm onSave={handleSaveTask} editingTask={editingTask} cancelEdit={() => setEditingTask(null)} />
      <TaskFilter onFilterChange={(f) => { setFilter(f); }} />
      <TaskList tasks={tasks} onEdit={handleEditTask} onDelete={handleDeleteTask} />
    </div>
  );
}
