import React, { useState, useEffect } from "react";

export default function IdeaForm({ token, onSave, editingIdea, cancelEdit }) {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [error, setError] = useState("");

  // Quand on reçoit une idée à éditer, on pré-remplit les champs
  useEffect(() => {
    if (editingIdea) {
      setTitle(editingIdea.title || "");
      setDescription(editingIdea.description || "");
      setStartDate(editingIdea.startDate || "");
      setEndDate(editingIdea.endDate || "");
    } else {
      // reset si pas d’édition en cours
      setTitle("");
      setDescription("");
      setStartDate("");
      setEndDate("");
    }
  }, [editingIdea]);

  // Validation simple avant sauvegarde
  function handleSubmit(e) {
    e.preventDefault();
    if (!title.trim()) {
      setError("Le titre est obligatoire.");
      return;
    }
    if (startDate && endDate && startDate > endDate) {
      setError("La date de début doit être avant la date de fin.");
      return;
    }
    setError("");
    // Appel du callback onSave en passant les données
    onSave({
      title: title.trim(),
      description: description.trim(),
      startDate,
      endDate,
      id: editingIdea?.id, // utile si on édite une idée existante
    });
    // Reset formulaire si besoin (optionnel)
    if (!editingIdea) {
      setTitle("");
      setDescription("");
      setStartDate("");
      setEndDate("");
    }
  }

  return (
    <form onSubmit={handleSubmit} style={{ maxWidth: 400, margin: "auto" }}>
      <h2>{editingIdea ? "Modifier une idée" : "Nouvelle idée"}</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <div style={{ marginBottom: 10 }}>
        <label>
          Titre <br />
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Titre de l'idée"
            required
          />
        </label>
      </div>
      <div style={{ marginBottom: 10 }}>
        <label>
          Description <br />
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Description"
            rows={4}
          />
        </label>
      </div>
      <div style={{ marginBottom: 10 }}>
        <label>
          Date de début <br />
          <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
          />
        </label>
      </div>
      <div style={{ marginBottom: 10 }}>
        <label>
          Date de fin <br />
          <input
            type="date"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
          />
        </label>
      </div>
      <button type="submit" style={{ marginRight: 10 }}>
        {editingIdea ? "Mettre à jour" : "Ajouter"}
      </button>
      {editingIdea && (
        <button type="button" onClick={cancelEdit}>
          Annuler
        </button>
      )}
    </form>
  );
}
