import React, { useState } from 'react';

export default function Register({ goToLogin }) {
  const [form, setForm] = useState({
    name: '',
    email: '',
    password: '',
  });

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  function handleSubmit(e) {
    e.preventDefault();
    if (!form.name) {
      alert('Le nom est obligatoire');
      return;
    }
    // Ensuite appeler ta fonction register
  }

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="name"
        value={form.name}
        onChange={handleChange}
        placeholder="Nom"
        required
      />
      <input
        type="email"
        name="email"
        value={form.email}
        onChange={handleChange}
        placeholder="Email"
        required
      />
      <input
        type="password"
        name="password"
        value={form.password}
        onChange={handleChange}
        placeholder="Mot de passe"
        required
      />
      <button type="submit">S'inscrire</button>
      <button type="button" onClick={goToLogin}>Déjà un compte ? Se connecter</button>
    </form>
  );
}
