import React from 'react';

export default function Navbar({ setView, handleLogout }) {
  return (
    <nav style={{ padding: 10, borderBottom: '1px solid #ccc', marginBottom: 20 }}>
      <button onClick={() => setView('tasks')} style={{ marginRight: 10 }}>Tâches</button>
      <button onClick={() => setView('ideas')} style={{ marginRight: 10 }}>Idées</button>
      <button onClick={handleLogout} style={{ float: 'right' }}>Déconnexion</button>
    </nav>
  );
}
