import React, { useState } from 'react';

export default function TaskFilter({ onFilterChange }) {
  // états locaux pour filtre statut, recherche texte, et tri
  const [statusFilter, setStatusFilter] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [sortField, setSortField] = useState('');
  const [sortOrder, setSortOrder] = useState('asc');

  // Appelé à chaque changement de filtre ou tri
  const handleChange = () => {
    onFilterChange({
      status: statusFilter,
      search: searchTerm,
      sortField,
      sortOrder,
    });
  };

  // Effets pour déclencher handleChange quand un état change
  React.useEffect(() => {
    handleChange();
  }, [statusFilter, searchTerm, sortField, sortOrder]);

  return (
    <div style={{ marginBottom: 20, display: 'flex', gap: 10, flexWrap: 'wrap', alignItems: 'center' }}>
      {/* Filtre par statut */}
      <select
        value={statusFilter}
        onChange={e => setStatusFilter(e.target.value)}
        style={{ padding: '6px' }}
      >
        <option value="">Tous les statuts</option>
        <option value="pending">En attente</option>
        <option value="in-progress">En cours</option>
        <option value="done">Terminé</option>
      </select>

      {/* Recherche par texte */}
      <input
        type="text"
        placeholder="Rechercher..."
        value={searchTerm}
        onChange={e => setSearchTerm(e.target.value)}
        style={{ padding: '6px', flexGrow: 1, minWidth: 150 }}
      />

      {/* Tri - champ */}
      <select
        value={sortField}
        onChange={e => setSortField(e.target.value)}
        style={{ padding: '6px' }}
      >
        <option value="">Trier par</option>
        <option value="title">Titre</option>
        <option value="dueDate">Date limite</option>
        <option value="status">Statut</option>
      </select>

      {/* Tri - ordre */}
      <select
        value={sortOrder}
        onChange={e => setSortOrder(e.target.value)}
        style={{ padding: '6px' }}
        disabled={!sortField}
      >
        <option value="asc">Ascendant</option>
        <option value="desc">Descendant</option>
      </select>
    </div>
  );
}
