import React from 'react';
import '../styles/Navbar.css';

export default function Navbar({ setView, token, handleLogout }) {
  return (
    <nav className="navbar">
      {!token ? (
        <>
          <button onClick={() => setView('login')} className="btn">
            Login
          </button>
          <button onClick={() => setView('register')} className="btn">
            Register
          </button>
        </>
      ) : (
        <>
          <button onClick={() => setView('tasks')} className="btn">
            Tasks
          </button>
          <button onClick={() => setView('ideas')} className="btn">
            Ideas
          </button>
          <button onClick={handleLogout} className="btn logout">
            Déconnexion
          </button>
        </>
      )}
    </nav>
  );
}
