import React, { useState } from "react";
import {
  Container,
  Typography,
  TextField,
  MenuItem,
  Grid,
  Select,
  FormControl,
  InputLabel,
  Card,
  CardContent
} from "@mui/material";

export default function App() {
  const [filter, setFilter] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");
  const [sortOption, setSortOption] = useState("az");

  const categories = ["Electronique", "Vêtements", "Maison"];

  const products = [
    { name: "Télévision", category: "Electronique" },
    { name: "Ordinateur portable", category: "Electronique" },
    { name: "T-shirt", category: "Vêtements" },
    { name: "Canapé", category: "Maison" },
    { name: "Chaise", category: "Maison" }
  ];

  // Filtrage
  let filteredProducts = products.filter((product) => {
    const matchesFilter = product.name
      .toLowerCase()
      .includes(filter.toLowerCase());
    const matchesCategory =
      selectedCategory === "" || product.category === selectedCategory;
    return matchesFilter && matchesCategory;
  });

  // Tri
  if (sortOption === "az") {
    filteredProducts.sort((a, b) => a.name.localeCompare(b.name));
  } else if (sortOption === "za") {
    filteredProducts.sort((a, b) => b.name.localeCompare(a.name));
  } else if (sortOption === "cat") {
    filteredProducts.sort((a, b) => a.category.localeCompare(b.category));
  }

  return (
    <Container>
      <Typography variant="h4" sx={{ my: 3, textAlign: "center" }}>
        🛍️ Ma Boutique
      </Typography>

      {/* Filtres */}
      <Grid container spacing={2} sx={{ mb: 3 }}>
        <Grid item xs={12} sm={4}>
          <TextField
            fullWidth
            label="Rechercher..."
            variant="outlined"
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
          />
        </Grid>

        <Grid item xs={12} sm={4}>
          <FormControl fullWidth>
            <InputLabel>Catégorie</InputLabel>
            <Select
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
              label="Catégorie"
            >
              <MenuItem value="">Toutes les catégories</MenuItem>
              {categories.map((cat, index) => (
                <MenuItem key={index} value={cat}>
                  {cat}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </Grid>

        <Grid item xs={12} sm={4}>
          <FormControl fullWidth>
            <InputLabel>Trier par</InputLabel>
            <Select
              value={sortOption}
              onChange={(e) => setSortOption(e.target.value)}
              label="Trier par"
            >
              <MenuItem value="az">Ordre alphabétique (A-Z)</MenuItem>
              <MenuItem value="za">Ordre alphabétique (Z-A)</MenuItem>
              <MenuItem value="cat">Par catégorie</MenuItem>
            </Select>
          </FormControl>
        </Grid>
      </Grid>

      {/* Liste des produits */}
      <Grid container spacing={2}>
        {filteredProducts.length > 0 ? (
          filteredProducts.map((p, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <Card>
                <CardContent>
                  <Typography variant="h6">{p.name}</Typography>
                  <Typography color="text.secondary">{p.category}</Typography>
                </CardContent>
              </Card>
            </Grid>
          ))
        ) : (
          <Typography
            variant="body1"
            color="error"
            sx={{ mt: 2, textAlign: "center", width: "100%" }}
          >
            Aucun produit trouvé
          </Typography>
        )}
      </Grid>
    </Container>
  );
}
