import React, { useState, useEffect } from 'react';
import Login from './components/Login';
import Register from './components/Register';
import Navbar from './components/Navbar';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';
import IdeaForm from './components/IdeaForm';
import IdeaTable from './components/IdeaTable';
import { login, register, getTasks, createTask, updateTask, deleteTask, getIdeas, createIdea, updateIdea, deleteIdea } from './services/api';

export default function App() {
  // Récupérer token en localStorage si présent
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  
  // Définir la page active selon si token existe ou pas
  // Si token, plateforme; sinon login par défaut
  const [page, setPage] = useState(token ? 'platform' : 'login');
  
  const [view, setView] = useState('tasks'); // vue active, 'tasks' ou 'ideas'
  const [tasks, setTasks] = useState([]);
  const [ideas, setIdeas] = useState([]);

  // Charger les données à chaque changement de token ou view
  useEffect(() => {
    if (!token) return;

    if (view === 'tasks') {
      getTasks(token)
        .then(data => setTasks(data))
        .catch(console.error);
    } else if (view === 'ideas') {
      getIdeas(token)
        .then(data => setIdeas(data))
        .catch(console.error);
    }
  }, [token, view]);

  // Gestion login réussie : stocker token, passer à la plateforme
  function handleLogin(newToken) {
    localStorage.setItem('token', newToken);
    setToken(newToken);
    setPage('platform');  // on passe à la plateforme après login
  }

  // Gestion logout : supprimer token, revenir au login
  function handleLogout() {
    localStorage.removeItem('token');
    setToken('');
    setTasks([]);
    setIdeas([]);
    setPage('login');  // revenir à la page login
  }

  // Affichage conditionnel selon page
  if (page === 'login') {
    return <Login onLogin={handleLogin} goToRegister={() => setPage('register')} />;
  }

  if (page === 'register') {
    return <Register goToLogin={() => setPage('login')} />;
  }

  // Page plateforme principale (tasks + ideas)
  return (
    <div>
      <Navbar setView={setView} handleLogout={handleLogout} />
      {view === 'tasks' && (
        <>
          <TaskForm token={token} onSave={newTask => setTasks([...tasks, newTask])} />
          <TaskList token={token} tasks={tasks} setTasks={setTasks} />
        </>
      )}
      {view === 'ideas' && (
        <>
          <IdeaForm token={token} onSave={newIdea => setIdeas([...ideas, newIdea])} />
          <IdeaTable ideas={ideas} setIdeas={setIdeas} />
        </>
      )}
    </div>
  );
}
