import React, { useState, useEffect } from "react";

export default function TaskFilter({ onFilterChange }) {
  const [status, setStatus] = useState("");
  const [search, setSearch] = useState("");
  const [sortField, setSortField] = useState("");
  const [sortOrder, setSortOrder] = useState("asc");

  useEffect(() => {
    onFilterChange({ status, search, sortField, sortOrder });
  }, [status, search, sortField, sortOrder, onFilterChange]);

  return (
    <div style={{ marginBottom: 20, display: "flex", gap: 10, flexWrap: "wrap" }}>
      <select value={status} onChange={e => setStatus(e.target.value)} style={{ padding: 6 }}>
        <option value="">Tous les statuts</option>
        <option value="pending">En attente</option>
        <option value="in-progress">En cours</option>
        <option value="done">Termin√©</option>
      </select>

      <input
        type="text"
        placeholder="Rechercher..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        style={{ flexGrow: 1, minWidth: 150, padding: 6 }}
      />

      <select value={sortField} onChange={e => setSortField(e.target.value)} style={{ padding: 6 }}>
        <option value="">Trier par</option>
        <option value="title">Titre</option>
        <option value="dueDate">Date limite</option>
        <option value="status">Statut</option>
      </select>

      <select
        value={sortOrder}
        onChange={e => setSortOrder(e.target.value)}
        style={{ padding: 6 }}
        disabled={!sortField}
      >
        <option value="asc">Ascendant</option>
        <option value="desc">Descendant</option>
      </select>
    </div>
  );
}
