import React, { useState, useEffect } from 'react';
import Navbar from './components/Navbar';
import Login from './components/Login';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';
import IdeaForm from './components/IdeaForm';
import IdeaTable from './components/IdeaTable';
import { getTasks, getIdeas } from './services/api';

export default function App() {
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [view, setView] = useState('tasks'); // Par défaut affiche plateforme
  const [tasks, setTasks] = useState([]);
  const [ideas, setIdeas] = useState([]);

  const handleLogin = async (jwt) => {
    localStorage.setItem('token', jwt);
    setToken(jwt);
    setView('tasks'); // Affiche la plateforme après login
    // Récupérer les données après login
    const fetchedTasks = await getTasks(jwt);
    setTasks(Array.isArray(fetchedTasks) ? fetchedTasks : []);
    const fetchedIdeas = await getIdeas(jwt);
    setIdeas(Array.isArray(fetchedIdeas) ? fetchedIdeas : []);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    setToken('');
    setTasks([]);
    setIdeas([]);
    setView('login');  // Affiche login si logout
  };

  useEffect(() => {
    if (!token) {
      setView('login'); // si pas de token, afficher login
      return;
    }
    if (view === 'tasks') {
      getTasks(token).then(data => setTasks(Array.isArray(data) ? data : []));
    } else if (view === 'ideas') {
      getIdeas(token).then(data => setIdeas(Array.isArray(data) ? data : []));
    }
  }, [view, token]);

  // Si pas connecté → affiche login (sans bouton register)
  if (!token) {
    return <Login onLogin={handleLogin} showRegister={false} />;
  }

  // Si connecté → affiche la plateforme
  return (
    <div style={{ maxWidth: 800, margin: 'auto', padding: 20 }}>
      <Navbar setView={setView} token={token} handleLogout={handleLogout} />

      {view === 'tasks' && (
        <>
          <TaskForm token={token} onSave={(newTask) => setTasks([...tasks, newTask])} />
          <TaskList token={token} tasks={tasks} setTasks={setTasks} />
        </>
      )}

      {view === 'ideas' && (
        <>
          <IdeaForm token={token} onSave={(newIdea) => setIdeas([...ideas, newIdea])} />
          <IdeaTable ideas={ideas} setIdeas={setIdeas} />
        </>
      )}
    </div>
  );
}
