export default function TaskList({ token, tasks, setTasks, onEdit }) {
  const safeTasks = Array.isArray(tasks) ? tasks : []; // ← sécurité

  async function handleDelete(id) {
    // ta logique suppression...
  }

  return (
    <div>
      <h3>Mes tâches</h3>
      {safeTasks.length === 0 ? (
        <p>Aucune tâche pour le moment.</p>
      ) : (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {safeTasks.map(task => (
            <li
              key={task._id}
              style={{
                border: '1px solid #ddd',
                padding: '10px',
                borderRadius: '8px',
                marginBottom: '8px',
                background: '#fff'
              }}
            >
              <strong>{task.title}</strong> — {task.status}
              {task.dueDate && <> — échéance : {new Date(task.dueDate).toLocaleDateString()}</>}
              <div style={{ marginTop: '8px' }}>
                <button
                  onClick={() => onEdit?.(task)} // le ? évite une erreur si onEdit est undefined
                  style={{
                    marginRight: '5px',
                    background: '#ffc107',
                    border: 'none',
                    padding: '6px 10px',
                    borderRadius: '4px'
                  }}
                >
                  Modifier
                </button>
                <button
                  onClick={() => handleDelete(task._id)}
                  style={{
                    background: '#dc3545',
                    color: '#fff',
                    border: 'none',
                    padding: '6px 10px',
                    borderRadius: '4px'
                  }}
                >
                  Supprimer
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
