import React, { useState, useEffect } from "react";

// ======== Navbar ========
function Navbar({ user, onLogout }) {
  return (
    <div style={{ backgroundColor: "#007BFF", color: "white", padding: "10px 20px", display: "flex", justifyContent: "space-between" }}>
      <span>Bienvenue, {user}</span>
      <button onClick={onLogout} style={{ backgroundColor: "red", color: "white", border: "none", padding: "5px 10px", cursor: "pointer" }}>
        Déconnexion
      </button>
    </div>
  );
}

// ======== Login ========
function Login({ onLogin, goRegister }) {
  const [username, setUsername] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (username.trim()) {
      onLogin(username);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Connexion</h2>
      <form onSubmit={handleSubmit}>
        <input value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Nom d'utilisateur" />
        <button type="submit">Se connecter</button>
      </form>
      <p>
        Pas encore inscrit ?{" "}
        <button onClick={goRegister} style={{ border: "none", background: "none", color: "blue", cursor: "pointer" }}>
          S'inscrire
        </button>
      </p>
    </div>
  );
}

// ======== Register ========
function Register({ onRegister, goLogin }) {
  const [username, setUsername] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (username.trim()) {
      onRegister(username);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Inscription</h2>
      <form onSubmit={handleSubmit}>
        <input value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Nom d'utilisateur" />
        <button type="submit">S'inscrire</button>
      </form>
      <p>
        Déjà inscrit ?{" "}
        <button onClick={goLogin} style={{ border: "none", background: "none", color: "blue", cursor: "pointer" }}>
          Se connecter
        </button>
      </p>
    </div>
  );
}

// ======== TaskFilter ========
function TaskFilter({ onFilterChange }) {
  const [status, setStatus] = useState("");
  const [sort, setSort] = useState("");

  useEffect(() => {
    onFilterChange({ status, sort });
  }, [status, sort]);

  return (
    <div style={{ marginBottom: 20 }}>
      <select value={status} onChange={(e) => setStatus(e.target.value)}>
        <option value="">Tous les statuts</option>
        <option value="pending">En attente</option>
        <option value="in-progress">En cours</option>
        <option value="done">Terminé</option>
      </select>
      <select value={sort} onChange={(e) => setSort(e.target.value)} style={{ marginLeft: 10 }}>
        <option value="">Tri par défaut</option>
        <option value="asc">Date croissante</option>
        <option value="desc">Date décroissante</option>
      </select>
    </div>
  );
}

// ======== TaskForm ========
function TaskForm({ onSave, editingTask, cancelEdit }) {
  const [title, setTitle] = useState("");
  const [status, setStatus] = useState("pending");
  const [dueDate, setDueDate] = useState("");
  const [description, setDescription] = useState("");

  useEffect(() => {
    if (editingTask) {
      setTitle(editingTask.title);
      setStatus(editingTask.status);
      setDueDate(editingTask.dueDate);
      setDescription(editingTask.description);
    } else {
      setTitle("");
      setStatus("pending");
      setDueDate("");
      setDescription("");
    }
  }, [editingTask]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!title.trim()) {
      alert("Le titre est obligatoire");
      return;
    }
    onSave({ title, status, dueDate, description });
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: 20 }}>
      <input value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Titre" required />
      <select value={status} onChange={(e) => setStatus(e.target.value)}>
        <option value="pending">En attente</option>
        <option value="in-progress">En cours</option>
        <option value="done">Terminé</option>
      </select>
      <input type="date" value={dueDate} onChange={(e) => setDueDate(e.target.value)} />
      <input value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" />
      <button type="submit">{editingTask ? "Modifier" : "Ajouter"}</button>
      {editingTask && <button onClick={cancelEdit}>Annuler</button>}
    </form>
  );
}

// ======== TaskList ========
const statusColors = { pending: "#f0ad4e", "in-progress": "#0275d8", done: "#5cb85c" };

function TaskList({ tasks, onEdit, onDelete }) {
  return (
    <table style={{ width: "100%", borderCollapse: "collapse" }}>
      <thead>
        <tr style={{ background: "#007BFF", color: "white" }}>
          <th>Titre</th>
          <th>Statut</th>
          <th>Date limite</th>
          <th>Description</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {tasks.length > 0 ? (
          tasks.map((task) => (
            <tr key={task.id}>
              <td>{task.title}</td>
              <td style={{ color: statusColors[task.status] }}>{task.status}</td>
              <td>{task.dueDate}</td>
              <td>{task.description}</td>
              <td>
                <button onClick={() => onEdit(task)}>✏️</button>
                <button onClick={() => onDelete(task.id)}>🗑️</button>
              </td>
            </tr>
          ))
        ) : (
          <tr>
            <td colSpan="5">Aucune tâche</td>
          </tr>
        )}
      </tbody>
    </table>
  );
}

// ======== App ========
export default function App() {
  const [view, setView] = useState("login");
  const [user, setUser] = useState("");
  const [tasks, setTasks] = useState([]);
  const [filters, setFilters] = useState({});
  const [editingTask, setEditingTask] = useState(null);

  const filteredTasks = tasks
    .filter((t) => !filters.status || t.status === filters.status)
    .sort((a, b) => {
      if (filters.sort === "asc") return new Date(a.dueDate) - new Date(b.dueDate);
      if (filters.sort === "desc") return new Date(b.dueDate) - new Date(a.dueDate);
      return 0;
    });

  const addTask = (task) => {
    if (editingTask) {
      setTasks(tasks.map((t) => (t.id === editingTask.id ? { ...task, id: t.id } : t)));
      setEditingTask(null);
    } else {
      setTasks([...tasks, { ...task, id: Date.now() }]);
    }
  };

  const deleteTask = (id) => setTasks(tasks.filter((t) => t.id !== id));

  if (view === "login") {
    return <Login onLogin={(u) => { setUser(u); setView("dashboard"); }} goRegister={() => setView("register")} />;
  }

  if (view === "register") {
    return <Register onRegister={(u) => { setUser(u); setView("dashboard"); }} goLogin={() => setView("login")} />;
  }

  if (view === "dashboard") {
    return (
      <div>
        <Navbar user={user} onLogout={() => setView("login")} />
        <div style={{ padding: 20 }}>
          <TaskFilter onFilterChange={setFilters} />
          <TaskForm onSave={addTask} editingTask={editingTask} cancelEdit={() => setEditingTask(null)} />
          <TaskList tasks={filteredTasks} onEdit={setEditingTask} onDelete={deleteTask} />
        </div>
      </div>
    );
  }

  return null;
}
