import React, { useState, useEffect } from 'react';

export default function TaskForm({ token, onSave, editingTask, cancelEdit }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [status, setStatus] = useState('pending');
  const [error, setError] = useState('');

  useEffect(() => {
    if (editingTask) {
      setTitle(editingTask.title || '');
      setDescription(editingTask.description || '');
      setDueDate(editingTask.dueDate ? editingTask.dueDate.substring(0, 10) : '');
      setStatus(editingTask.status || 'pending');
    } else {
      setTitle('');
      setDescription('');
      setDueDate('');
      setStatus('pending');
    }
    setError('');
  }, [editingTask]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title.trim()) {
      setError('Le titre est requis');
      return;
    }

    // Prépare les données à envoyer, supprime les champs vides inutiles
    const taskData = {
      title: title.trim(),
      description: description.trim() || undefined,
      dueDate: dueDate || undefined,
      status,
    };

    try {
      await onSave(taskData);
      setError('');
    } catch (err) {
      setError('Erreur lors de la sauvegarde, réessaye.');
      console.error(err);
    }
  };

  return (
    <form 
      onSubmit={handleSubmit} 
      style={{ padding: '15px', border: '1px solid #ccc', borderRadius: '8px', marginBottom: '20px', background: '#f9f9f9' }}
    >
      <h3>{editingTask ? 'Modifier la tâche' : 'Ajouter une tâche'}</h3>
      
      {error && <p style={{color: 'red'}}>{error}</p>}

      <input 
        type="text"
        placeholder="Titre"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
        style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
      />
      
      <textarea
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        style={{ width: '100%', padding: '8px', marginBottom: '10px' }}
      />
      
      <input 
        type="date"
        value={dueDate}
        onChange={(e) => setDueDate(e.target.value)}
        style={{ padding: '8px', marginBottom: '10px', width: '100%' }}
      />
      
      <select 
        value={status}
        onChange={(e) => setStatus(e.target.value)}
        style={{ padding: '8px', marginBottom: '10px', width: '100%' }}
      >
        <option value="pending">En attente</option>
        <option value="completed">Terminée</option>
      </select>

      <button type="submit" style={{ padding: '8px 12px', background: '#28a745', color: '#fff', border: 'none', borderRadius: '4px' }}>
        {editingTask ? 'Mettre à jour' : 'Ajouter'}
      </button>

      {editingTask && (
        <button 
          type="button" 
          onClick={cancelEdit} 
          style={{ padding: '8px 12px', background: '#6c757d', color: '#fff', border: 'none', borderRadius: '4px', marginLeft: '8px' }}
        >
          Annuler
        </button>
      )}
    </form>
  );
}
